import { useState } from 'react';
import { data } from './data';
import './App.css';

function App() {
  const [place, setPlace] = useState(data)
  const [showText, setShowText] = useState(false)

  const showTextClick = (item) =>{
    item.showMore = !item.showMore;
    setShowText(!showText)
  }


  return (
    <div >
      <div className='header'>
      <h1>{place.length} Most Beautiful Places in Colorado</h1>
      </div>
      {place.map((item =>{
        const {id, place, image, description, showMore} = item;
        return(
          <div className='container'>
            <div>
              <h2>{id} {place}</h2>
              <img src={image} width="800px" alt="photo"/>
              <div className="">
            <p>{showMore ? description : description.substring(0, 90) + "..."}
            <button onClick = {() => showTextClick(item)}> {showMore ? "Show less" : "Show more"}</button>
            </p>
          </div>
            </div>
          </div>
        )
      }))}

      <div>

      </div>
    </div>
  );
}

export default App;
